import { useParams } from "react-router-dom";
import { projects } from "../Helpers/projectsList";
import BtnGitHub from "../Components/BtnGitHub";
import {Swiper, SwiperSlide, useSwiper} from 'swiper/react';
import "swiper/css"


const Project = () => {

    const {id} = useParams();   
    const project = projects[id];
    const imagesBig = project.imagesBig;
    return ( <main className="section">
    <div className="container">
        <div className="project-details">
            <h1 className="title-1">{project.title}</h1>

            <Swiper 
                slidesPerView={1}
                spaceBetween={50}
                loop={true}
                style={{width:"100%"}}>
                <SliderButtons/>    
                {imagesBig.map((imageBig,index)=>(
                <SwiperSlide key={index}>
                    <img src={imageBig} alt={project.title}className="project-details__cover"/>
                </SwiperSlide>                            
                    ))}
            </Swiper>

            <div className="project-details__skills">
                <p> Used Skills: <em>{project.skills}</em></p>
            </div>
            <div className="project-details__desc">
                <p>{project.description}</p>
            </div>
            
           {project.gitHubLink &&(<BtnGitHub/>)}
           
        </div>
    </div>
</main>
);

}
const SliderLeftButton = ()=>{
    const swiper = useSwiper();
    return (
        <div className="flexCenter r-buttons"
            style={{width:"100%",
                    zIndex: "3",
                    position:"absolute", top:"50%", 
                    transform:"translateY(-50%)"

            }}
        >
            <div className="slideSwipeBtn_left">
                <button onClick={()=>swiper.slidePrev()}>&lt;</button>
            </div>
            <div className="slideSwipeBtn_right">
                <button onClick={()=>swiper.slideNext()}>&gt;</button>
            </div>
        </div>

    )
}
export default Project;
